void game_start(){
  currentName[0] = 'A';
  currentName[1] = 'A';
  currentName[2] = 'A';
  currentName[3] = 'A';
  currentName[4] = '\0';

  nameIndex = 0;
  currentChar = 'A';

	score = 0;
  restart_game = 0;
	game_over = 0;
	
	pacman_anim_state = 0;

	pacmanX = 62;
	pacmanY = 1;
	pacmanDirection = Down;

	//Initialize ghost positions
	Inky.pos.x = 4;
	Inky.pos.y = 18;
	Inky.scatterTarget.x = 4;
	Inky.scatterTarget.y = 18;

	Blinky.pos.x = 120;
	Blinky.pos.y = 18;
	Blinky.scatterTarget.x = 120;
	Blinky.scatterTarget.y = 18;

	Pinky.pos.x = 120;
	Pinky.pos.y = 2;
	Pinky.scatterTarget.x = 120;
	Pinky.scatterTarget.y = 2;

	scatterTimer = 7;
	chaseTimer = 20;
	scatterCount = 4;
	chaseMode = 0;
	frightenedMode = 0;
	timer = 0;			//Incremented every second to keep track of the different modes	

  clear_display_buffer(display_buffer);
	display_map(map, 604, display_buffer);
  int i, j;
  for(i = 0; i < 23; i++){
    for(j = 0; j < 128; j++){
      mapPoints[i][j] = mapPoints_copy[i][j];
    }
  }

	display_update(display_buffer);
}

void update_ghosts(){
  clear_entity(ghostPixels, 14, Inky.pos.x, Inky.pos.y, display_buffer);    
  clear_entity(ghostPixels, 14, Blinky.pos.x, Blinky.pos.y, display_buffer); 
  clear_entity(ghostPixels, 14, Pinky.pos.x, Pinky.pos.y, display_buffer); 

  update_ghost(&Inky);
  update_ghost(&Blinky);
  update_ghost(&Pinky);

  update_entity(ghostPixels, 14, Inky.pos.x, Inky.pos.y, display_buffer);
  update_entity(ghostPixels, 14, Blinky.pos.x, Blinky.pos.y, display_buffer);
  update_entity(ghostPixels, 14, Pinky.pos.x, Pinky.pos.y, display_buffer);
}

void update_ghost_mode(){
  if(chaseMode && chaseTimer > 0){
    update_pinky_target();
    update_blinky_target();
    update_inky_target();
  }
  else if(chaseMode && chaseTimer <= 0 && scatterCount > 0){
    //Set to scatter mode
    Pinky.target = Pinky.scatterTarget;
    Inky.target = Inky.scatterTarget;
    Blinky.target = Blinky.scatterTarget;

    chaseMode = 0;
    chaseTimer = 20;
    scatterTimer = 8;
  }
  if(frightenedMode){
    chaseMode = 0;
    Pinky.target = Pinky.scatterTarget;
    Inky.target = Inky.scatterTarget;
    Blinky.target = Blinky.scatterTarget;
  }

  if(!chaseMode && scatterTimer == 0){
    scatterCount--;

    chaseMode = 1;
    chaseTimer = 20;
    scatterTimer = 7;
  }
}

void update_pinky_target(){
  switch(pacmanDirection){
      case Up:  
        //When pac-man faces up this is Pinky's target, for historical reasons...
        Pinky.target.x = pacmanX - 4;
        Pinky.target.y = pacmanY - 4;
        break;
      case Down: 
        Pinky.target.x = pacmanX;
        Pinky.target.y = pacmanY + 8;
      case Right: 
        Pinky.target.x = pacmanX + 8;
        Pinky.target.y = pacmanY;
        break;
      case Left:
        Pinky.target.x = pacmanX - 4;
        Pinky.target.y = pacmanY;
        break;
    }
}

void update_blinky_target(){
  struct Coord baseTarget;
  switch(pacmanDirection){
      case Up:  
        baseTarget.x = pacmanX - 2;
        baseTarget.y = pacmanY - 2;
        break;
      case Down: 
        baseTarget.x = pacmanX;
        baseTarget.y = pacmanY + 6;
      case Right: 
        baseTarget.x = pacmanX + 6;
        baseTarget.y = pacmanY;
        break;
      case Left:
        baseTarget.x = pacmanX - 2;
        baseTarget.y = pacmanY;
        break;
    } 
  
  int differenceX = Blinky.pos.x - baseTarget.x;
  int differenceY = Blinky.pos.y - baseTarget.y;

  Blinky.target.x = baseTarget.x + (-1*differenceX);
  Blinky.target.y = baseTarget.y + (-1*differenceY);
}

void update_inky_target(){
  Inky.target.x = pacmanX;
  Inky.target.y = pacmanY;
}

void user_isr( void ) {
  if(IFS(0) & (1 << 8)){
      IFS(0) &= ~(1 << 8);   //reset interrupt timer overflow status flag

    if(view_highscore) {
      view_highscore_menu();
      return;
    }
    if(restart_game){
      game_start();
      return;
    }
    if(game_over) {
      select_highscore_name();
      return;
    }
    if(from_highscore){
      clear_display_buffer(display_buffer);
	    display_map(map, 604, display_buffer);
      from_highscore = 0;
    }


    timer++;
    if(timer == 5){
      timer = 0;
      if(frightenedMode){
        if(frightenedTimer>0)frightenedTimer--;
        else{
        frightenedMode = 0;
        chaseMode = 1;
        chaseTimer = 20;
        } 
      }
      if(chaseMode && scatterCount > 0) chaseTimer--;
      else scatterTimer--;
      
    }

    update_ghost_mode();
    update_pacman();
    update_score();
    update_ghosts();

    if(pacman_anim_state) update_entity(pacmanClosed, 12, pacmanX, pacmanY, display_buffer);
    else update_entity(pacmanOpen[pacmanDirection], 8, pacmanX, pacmanY, display_buffer);

    update_food(foods, 376, superFoods, 60, mapPoints, display_buffer);
		display_update(display_buffer);
  }
}

void update_score(){
  display_string(3, 0, "score:", 7, display_buffer);
  display_string(3, 37, itoaconv(score), 4, display_buffer);
}

void game_init(){
    enable_interrupt();

    TMR2 = 0;     //reset timer to 0
    T2CONSET = (1 << 15);   //set bit 15 of T2CON to 1, corresponding to turning the timer on
    T2CONSET = (0b111 << 4);      //Set timescale value to 256    
    PR2 = 62300;                  //This will make the timer overflow eveery 0.2s

    IEC(0) |= (1 << 8);   //enable timer interrupt
    IPC(2) |=  0b1100;   //set priority of timer 2
}

void check_button_pressed(){
    //Check if any button has been pressed
  int buttonPressed = 0;
  if((buttonPressed = getbtns())){
    if(buttonPressed & 0b1000){           //BTN1 was pressed
      pacmanDirection = Right;
    }
    if(buttonPressed & 0b1) {         //BTN2 was pressed
      pacmanDirection = Left;
    }   
    if(buttonPressed & 0b10) {        //BTN3 was pressed
      pacmanDirection = Down;
    }
    if(buttonPressed & 0b100){         //BTN4 was pressed
      pacmanDirection = Up;
    }
  }
}

void change_name(){
  int buttonPressed = 0;
  if((buttonPressed = getbtns())){          //BTN1 was pressed
    if(buttonPressed & 0b1000){
        nameIndex = (nameIndex + 1) % 4;
        currentChar = currentName[nameIndex];
    }
    if(buttonPressed & 0b1) {         //BTN2 was pressed
        nameIndex--;
        if(nameIndex < 0) nameIndex = 3;
        currentChar = currentName[nameIndex];
    }   
    if(buttonPressed & 0b10) {        //BTN3 was pressed
        currentChar--;
        if(currentChar < 65) currentChar = 90;
        currentName[nameIndex] = currentChar;
    }
    if(buttonPressed & 0b100){         //BTN4 was pressed
        currentChar++;
        if(currentChar > 90) currentChar = 65;
        currentName[nameIndex] = currentChar;
    }
  } 
}

void check_switches(){
  int switches = getsw();
  if(switches & 1){   //Switch 1 is turned on
    view_highscore = 1;
  }
  else {
    view_highscore = 0;
  }
  if((switches & 2) != (prev_switch & 2)){
    restart_game = 1;
  }
  prev_switch = switches;
}

void select_highscore_name(){
  clear_display_buffer(display_buffer);
  change_name();
  display_string(1, 10, "Write your name: ", 18, display_buffer);
  display_string(2, 10, currentName, 5, display_buffer);
  display_update(display_buffer);
}

void view_highscore_menu(){
  clear_display_buffer(display_buffer);
  int i,p;
  if(game_over && !restart_game){
    currentName[nameIndex] = currentChar;
    i = 0;
    while(i < 4 && score < score_board[i]) {
      i++;
    }
    if(i < 4){        //If it's >= 4 then it should not be written to score board
      int j;
      for(j = 3; j > i; j--) {
        score_board[j] = score_board[j-1];
        for(p = 0; p < 4; p++){
          score_board_names[j][p] = score_board_names[j-1][p];
        }
      }
      score_board[i] = score;
      for(p = 0; p < 4; p++){
        score_board_names[i][p] = currentName[p];
      }
    }
    restart_game = 1;
  }

  for(i = 0; i < 4; i++){
    display_string(i, 10, score_board_names[i], 5, display_buffer);
    display_string(i, 70, itoaconv(score_board[i]), 4, display_buffer);
  }
  if(!game_over){
    from_highscore = 1;
  }
  display_update(display_buffer);
}

